package ForLoop;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//for loop = executes a block of code a limited amount of times
		
		for(int i=0; i<=10; i++) {
			System.out.println(i);
		}
		
		System.out.println("=================");
		
		for(int i = 10; i>=0; i--) {
			
			System.out.println(i);
			
		}
		
		System.out.println("Happy new year!");
		
		System.out.println("=================");
		
		for(int i = 10; i>=0; i-=2){
			System.out.println(i);
		}
		
		System.out.println("Happy new year!");

	}

}
